// Helper function to get country data by ISO alpha-3 code
function getCountryByAlpha3(alpha3Code) {
    const alpha2Code = countryCodeMapping[alpha3Code];
    if (alpha2Code && countriesData[alpha2Code]) {
        return { code: alpha2Code, ...countriesData[alpha2Code] };
    }
    return null;
}

// Get all countries as an array
function getAllCountries() {
    return Object.entries(countriesData).map(([code, data]) => ({
        code,
        ...data
    }));
}
